package com.java.Operator;

public class Assignment_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		a+=10;
		System.out.println(a);
		a*=20;
		System.out.println(a);
		a-=10;
		System.out.println(a);
		a/=20;
		System.out.println(a);
		
	}

}
